console.log(`Skip platform banner: ${skipPlatformBanner}`);

return ratios.map(ratio => ({
  json: {
    ...config,
    ratio: ratio,
    skipPlatformBanner: skipPlatformBanner,
    _ratios: ratios,
    _platforms: platforms
  }
}));

// Prepare data for [08] Add Platform Banners
// Process ALL items, not just first
const allItems = $input.all();

return allItems.map(item => {
  const config = item.json;
  return {
    json: {
      video_url: config.final_video_url,
      avatar_name: config.avatar_name,
      platforms: config._platforms,
      ratio: config.ratio,
      _original_config: config
    }
  };
});

// No banners needed - create platform_videos array with single video
// Process ALL items, not just first
const allItems = $input.all();

return allItems.map(item => {
  const config = item.json;
  return {
    json: {
      ...config,
      platform_videos: [{
        platform: 'none',
        ratio: config.ratio,
        video_url: config.final_video_url
      }]
    }
  };
});

// Pass through results from [08]
// [08] Format Output already spreads _original_config (with email etc) into root
// and adds platform_videos - so just pass through all items
const allItems = $input.all();
return allItems.map(item => ({ json: item.json }));

// Flatten all platform_videos into single array
const allItems = $json.all_videos || [];

// Get base config from first item (has email, template_id, etc)
const baseConfig = allItems[0] || {};

// Get generated_copy from webhook input (preserved through the flow)
// Try multiple sources in case it's in different places
const generatedCopy = baseConfig.generated_copy 
  || $('Webhook').first().json.body.generated_copy 
  || [];

// Collect all videos
const allVideos = [];
for (const item of allItems) {
  const videos = item.platform_videos || [];
  allVideos.push(...videos);
}

console.log('Total videos generated:', allVideos.length);
console.log('Email:', baseConfig.email);
console.log('Generated copy items:', generatedCopy.length);

// Build video links for email
const videoLinks = allVideos.map(v => {
  const label = v.platform !== 'none'
    ? `${v.ratio} - ${v.platform}`
    : v.ratio;
  return `<a href='${v.video_url}'>${label}</a>`;
}).join('<br/>');

// Build generated copy HTML for email
let copyHtml = '';
if (generatedCopy.length > 0) {
  copyHtml = '<br/><hr style="border: none; border-top: 1px solid #ddd; margin: 20px 0;"/>';
  copyHtml += '<p><strong>Platform Copy:</strong></p>';

  for (const item of generatedCopy) {
    const platform = item.platform?.toUpperCase() || 'UNKNOWN';
    const copy = item.copy || {};

    copyHtml += '<div style="margin: 15px 0; padding: 15px; background: #f9f9f9; border-radius: 8px;">';
    copyHtml += `<p style="margin: 0 0 10px 0; font-weight: bold; color: #333;">${platform}</p>`;

    if (platform === 'META') {
      if (copy.primary_text) {
        copyHtml += `<p style="margin: 8px 0;"><strong>Primary Text:</strong><br/>${copy.primary_text.replace(/\n/g, '<br/>')}</p>`;
      }
      if (copy.headline) {
        copyHtml += `<p style="margin: 8px 0;"><strong>Headline:</strong> ${copy.headline}</p>`;
      }
      if (copy.description) {
        copyHtml += `<p style="margin: 8px 0;"><strong>Description:</strong> ${copy.description}</p>`;
      }
    } else if (platform === 'TIKTOK') {
      if (copy.caption) {
        copyHtml += `<p style="margin: 8px 0;"><strong>Caption:</strong><br/>${copy.caption}</p>`;
      }
      if (copy.hashtags) {
        const hashtagStr = Array.isArray(copy.hashtags) ? copy.hashtags.join(' ') : copy.hashtags;
        copyHtml += `<p style="margin: 8px 0;"><strong>Hashtags:</strong> ${hashtagStr}</p>`;
      }
    } else if (platform === 'YOUTUBE') {
      if (copy.title) {
        copyHtml += `<p style="margin: 8px 0;"><strong>Title:</strong> ${copy.title}</p>`;
      }
      if (copy.description) {
        copyHtml += `<p style="margin: 8px 0;"><strong>Description:</strong><br/>${copy.description.replace(/\n/g, '<br/>')}</p>`;
      }
      if (copy.tags) {
        const tagsStr = Array.isArray(copy.tags) ? copy.tags.join(', ') : copy.tags;
        copyHtml += `<p style="margin: 8px 0;"><strong>Tags:</strong> ${tagsStr}</p>`;
      }
    }

    copyHtml += '</div>';
  }
}

return [{
  json: {
    ...baseConfig,
    all_videos: allVideos,
    video_count: allVideos.length,
    video_links_html: videoLinks,
    generated_copy: generatedCopy,
    generated_copy_html: copyHtml,
    final_video_url: allVideos[0]?.video_url || baseConfig.final_video_url
  }
}];

