// Split config into one item per ratio
const config = $json;
const ratios = config.ratios || ['9:16'];
const platforms = config.platforms || [];

// Determine if we should skip platform banner in Creatomate
const skipPlatformBanner = platforms.length > 0;

console.log(`Splitting into ${ratios.length} ratio(s)`);
console.log(`Skip platform banner: ${skipPlatformBanner}`);

return ratios.map(ratio => ({
  json: {
    ...config,
    ratio: ratio,
    skipPlatformBanner: skipPlatformBanner,
    _ratios: ratios,
    _platforms: platforms
  }
}));

// Prepare data for [08] Add Platform Banners
// Process ALL items, not just first
const allItems = $input.all();

return allItems.map(item => {
  const config = item.json;
  return {
    json: {
      video_url: config.final_video_url,
      avatar_name: config.avatar_name,
      platforms: config._platforms,
      ratio: config.ratio,
      _original_config: config
    }
  };
});

// No banners needed - create platform_videos array with single video
// Process ALL items, not just first
const allItems = $input.all();

return allItems.map(item => {
  const config = item.json;
  return {
    json: {
      ...config,
      platform_videos: [{
        platform: 'none',
        ratio: config.ratio,
        video_url: config.final_video_url
      }]
    }
  };
});

// Pass through results from [08]
// [08] Format Output already spreads _original_config (with email etc) into root
// and adds platform_videos - so just pass through all items
const allItems = $input.all();
return allItems.map(item => ({ json: item.json }));

// Flatten all platform_videos into single array
const allItems = $json.all_videos || [];

// Get base config from first item (has email, template_id, etc)
const baseConfig = allItems[0] || {};

// Collect all videos
const allVideos = [];
for (const item of allItems) {
  const videos = item.platform_videos || [];
  allVideos.push(...videos);
}

console.log(`Total videos generated: ${allVideos.length}`);
console.log(`Email: ${baseConfig.email}`);

// Build video links for email
const videoLinks = allVideos.map(v => {
  const label = v.platform !== 'none' 
    ? `${v.ratio} - ${v.platform}` 
    : v.ratio;
  return `<a href='${v.video_url}'>${label}</a>`;
}).join('<br/>');

return [{
  json: {
    ...baseConfig,
    all_videos: allVideos,
    video_count: allVideos.length,
    video_links_html: videoLinks,
    final_video_url: allVideos[0]?.video_url || baseConfig.final_video_url
  }
}];

