// Get webhook body and DB results from earlier nodes
const webhookData = $("Webhook").first().json;
const webhookBody = webhookData.body;
const avatarResult = $("Lookup Avatar Config").first().json;
const backgroundResult = $("Lookup Background").first()?.json || null;
const wideOverrideResult = $("Lookup Wide Override Default").first()?.json || null;

// ============================================
// Build Configuration
// ============================================

// Merge avatar config
const config = {
  ...webhookBody,
  avatar_name: avatarResult.display_name || webhookBody.avatar_name,
  voice_id: avatarResult.voice_id || webhookBody.voice_id,
  voice_settings: avatarResult.voice_settings || null,  // Let API use defaults if not configured
  photo_avatar_id: avatarResult.photo_avatar_id,
  motion_avatar_id: avatarResult.motion_avatar_id,
  avatar_id: avatarResult.motion_avatar_id,
  person_full_name: avatarResult.full_name,
  person_display_name: avatarResult.display_name
};

// ============================================
// Background Handling
// ============================================

if (backgroundResult?.rendered_urls) {
  // User selected a background
  config.background_rendered_urls = backgroundResult.rendered_urls;
  console.log(`Background rendered URLs: ${JSON.stringify(config.background_rendered_urls)}`);
} else if (wideOverrideResult?.rendered_urls?.['9:16']) {
  // No user selection, but template has wide override - use default 9:16
  config.avatar_background_url = wideOverrideResult.rendered_urls['9:16'];
  console.log(`Avatar background (wide override): ${config.avatar_background_url}`);
}

console.log(`Avatar config built for: ${config.person_display_name || config.avatar_name}`);
console.log(`Voice ID: ${config.voice_id}`);
console.log(`Voice settings: ${JSON.stringify(config.voice_settings)}`);
console.log(`HeyGen Avatar ID: ${config.avatar_id}`);

return [{ json: { body: config } }];

const formatResponse = $("Format Response").first().json;
return [{
  json: {
    workflow_name: "[01] Orchestrate Video Creation",
    error: "Failed to send video completion email",
    details: {
      email: formatResponse.email,
      video_count: formatResponse.video_count,
