        cargoFeatures.push('with power liftgate');
      }

      // Truck bed features
      if (equipment.truck_bed_features && equipment.truck_bed_features.length > 0) {
        if (equipment.truck_bed_features.some(f => /bed liner/i.test(f))) {
          cargoFeatures.push('spray-in bed liner');
        }
        if (equipment.truck_bed_features.some(f => /multipro/i.test(f))) {
          cargoFeatures.push('MultiPro tailgate');
        }
      }

      return cargoFeatures.join(', ');

    case 'steering_wheel':
      const wheelFeatures = [];

      if (equipment.has_heated_wheel || visual.controls_visible?.heated) {
        wheelFeatures.push('heated steering wheel');
      }

      if (visual.controls_visible?.audio || visual.controls_visible?.cruise) {
        wheelFeatures.push('with steering wheel controls');
      }

      if (visual.paddle_shifters) {
        wheelFeatures.push('and paddle shifters');
      }

      return wheelFeatures.length > 0 ? wheelFeatures.join(' ') : 'multifunction steering wheel';

    case 'second_row_seats':
      const secondRowFeatures = [];

      if (visual.space_rating === 'spacious' || visual.space_rating === 'generous') {
        secondRowFeatures.push('spacious rear seating');
      } else {
        secondRowFeatures.push('rear seating');
      }

      if (visual.features_visible && visual.features_visible.length > 0) {
        if (visual.features_visible.some(f => /climate/i.test(f))) {
          secondRowFeatures.push('with rear climate control');
        }
      }

      return secondRowFeatures.join(' ');

    case 'third_row_seats':
      return 'third row seating for seven passengers';

    case 'instrument_cluster':
      if (visual.cluster_type === 'digital') {
        return 'digital instrument cluster';
      } else if (visual.cluster_type === 'hybrid') {
        return 'advanced gauge cluster with digital display';
      }
      return null; // Don't highlight basic analog clusters

    case 'special_feature':
      if (visual.feature_identified) {
        return visual.feature_identified;
      }
      return null;

    case 'infotainment_other':
      const otherFeatures = [];

      if (visual.showing === 'navigation' || equipment.has_navigation) {
        otherFeatures.push('navigation system');
      }

      if (equipment.audio_system && /bose|harman|revel|premium/i.test(equipment.audio_system)) {
        otherFeatures.push(equipment.audio_system);
      }

      if (otherFeatures.length === 0) {
        otherFeatures.push('touchscreen infotainment');
      }

      return otherFeatures.join(' with ');

    default:
      return null;
  }
}

