const allGuidanceRules = $('Fetch Guidance Rules').all().map(item => item.json) || [];
const config = $('Pass Config').first().json;
const clientId = config.client_id || 'ccc';
const avatarName = config.avatar_name || '*';

// Filter rules
const activeRules = allGuidanceRules.filter(rule => {
  if (rule.active !== true && rule.active !== 'TRUE' && rule.active !== 'true') return false;
  if (rule.client_id !== clientId) return false;
  if (rule.avatar_name !== '*' && rule.avatar_name !== avatarName) return false;
  return true;
});

// Group by category
const rulesByCategory = {
  facts: [],
  pronunciation: [],
  style: [],
  compliance: []
};

activeRules.forEach(rule => {
  const category = (rule.category || '').toLowerCase();
  if (rulesByCategory[category]) {
    rulesByCategory[category].push(rule.rule_text);
  }
});

// Build the section string
let guidanceRulesSection = '';
if (activeRules.length > 0) {
  guidanceRulesSection = `\nğŸ“ CUSTOM GUIDANCE RULES:\n`;
  if (rulesByCategory.facts.length > 0) {
    guidanceRulesSection += `\nFACTS (use these exactly):\n${rulesByCategory.facts.map(r => `- ${r}`).join('\n')}\n`;
  }
  if (rulesByCategory.pronunciation.length > 0) {
    guidanceRulesSection += `\nPRONUNCIATIONS:\n${rulesByCategory.pronunciation.map(r => `- ${r}`).join('\n')}\n`;
  }
  if (rulesByCategory.style.length > 0) {
    guidanceRulesSection += `\nSTYLE:\n${rulesByCategory.style.map(r => `- ${r}`).join('\n')}\n`;
  }
  if (rulesByCategory.compliance.length > 0) {
    guidanceRulesSection += `\nCOMPLIANCE:\n${rulesByCategory.compliance.map(r => `- ${r}`).join('\n')}\n`;
  }
}

return [{
  json: {
    ...config,
    guidanceRulesSection,
    rulesByCategory  // in case you need individual categories
  }
}];
