const config = $json;

const selectedPhotos = config.selected_photos || {};

// Get VIN from vehicles array
const vin = config.vehicles?.[0]?.vin;

// Validate we have required data
if (!vin) {
  throw new Error('VIN missing - needed for Cloudinary lookup');
}

if (!selectedPhotos.hero_exterior) {
  throw new Error('No hero_exterior photo found in selected_photos!');
}

const heroPhoto = selectedPhotos.hero_exterior;

// Check if BG-removed version exists in Cloudinary
const bgRemovedUrl = `https://res.cloudinary.com/dtpqxuwby/image/upload/${vin}.png`;
console.log(`ğŸ” Checking Cloudinary: ${bgRemovedUrl}`);

let heroBackgroundRemoved = null;
let needsProcessing = false;

try {
  const response = await this.helpers.httpRequest({
    method: 'HEAD',
    url: bgRemovedUrl,
    returnFullResponse: true,
    ignoreHttpStatusErrors: true,
    timeout: 5000
  });

  if (response.statusCode === 200) {
    console.log('âœ… BG-removed hero found in Cloudinary!');
    heroBackgroundRemoved = bgRemovedUrl;
    needsProcessing = false;

    selectedPhotos.hero_exterior.photo_url = bgRemovedUrl;
  } else {
    console.log(`Status code: ${response.statusCode}`);
    needsProcessing = true;
  }
} catch (error) {
  needsProcessing = true;
}

return [{
  json: {
    ...config,

    selected_photos: selectedPhotos,
    hero_photo_url: heroPhoto.photo_url,
    hero_photo_index: heroPhoto.photo_index,
    hero_bg_removed_url: heroBackgroundRemoved,
    needs_bg_removal: needsProcessing,
    cloudinary_checked: true
  }
}];

const config = $json;
const vehicle = config.vehicles[0];
const equipment = vehicle.equipment;

// Split equipment into individual features
const features = equipment.split('|').map(f => f.trim()).filter(f => f);

// Initialize category arrays
const categories = {
  enginePower: [],
  transmission: [],
  drivetrain: [],
  brakingStability: [],
  safetyAirbags: [],
  driverAssist: [],
  exteriorLighting: [],
  roofsGlass: [],
  interiorComfort: [],
  seatingConfig: [],
  infotainmentAudio: [],
  navigationDisplay: [],
  securityAntiTheft: [],
  towingOffRoad: [],
  packages: [],
  wheelsTires: []
};

// Helper function to categorize features
const categorizeFeature = (feature) => {
  const f = feature.toLowerCase();

  // 1. Engine & Power
  if (/^(v6|v8|4-cyl|3-cyl|i4|i6)/.test(f) || 
      /(ecoboost|ecotec|hybrid|turbo|hemi|gdi|vtec|multiair|liter|electric)/i.test(f)) {
    categories.enginePower.push(feature);
  }

  // 2. Transmission
  if (/(automatic|auto|manual|cvt|spd|drive unit|shiftronic|selshft|xtronic|overdrive)/i.test(f)) {
    categories.transmission.push(feature);
  }

  // 3. Drivetrain
  if (/^(fwd|rwd|awd|4wd|2wd)$/i.test(f) || /wheel drive/i.test(f)) {
    categories.drivetrain.push(feature);
  }

  // 4. Braking & Stability
  if (/(abs|stability|stabilitrak|advancetrac|rollover|hill start|hill descent|traction|vehicle dynamic)/i.test(f)) {
    categories.brakingStability.push(feature);
  }

  // 5. Safety - Airbags
  if (/air bags|airbag|tire pressure monitoring/i.test(f)) {
    categories.safetyAirbags.push(feature);
  }

  // 6. Driver Assist / ADAS
  if (/(camera|park assist|parksense|parking sensor|distance warning|sonar|blind|lane|collision|propilot|safety shield|adaptive cruise|intelligent cruise)/i.test(f)) {
    categories.driverAssist.push(feature);
  }

  // 7. Exterior & Lighting
  if (/(headlamp|headlight|xenon|hid|led|fog lamp|daytime running|bed liner|tailgate|spoiler|roof rack|running board|tonneau|shell|cap|sliding rear window)/i.test(f)) {
    categories.exteriorLighting.push(feature);
  }

  // 8. Roofs & Glass
  if (/(roof:|moonroof|sunroof|panoramic|skylight|privacy glass)/i.test(f)) {
    categories.roofsGlass.push(feature);
  }

  // 9. Interior Comfort
  if (/(air conditioning|climate|keyless|remote start|power door|power window|power liftgate|power tailgate|power trunk|tilt|telescoping|steering wheel: heated|garage door)/i.test(f)) {
    categories.interiorComfort.push(feature);
  }

  // 10. Seating & Configuration
  if (/(seat|passenger seating|leather)/i.test(f) && !/steering/i.test(f)) {
    categories.seatingConfig.push(feature);
  }

  // 11. Infotainment & Audio
  if (/(am\/fm|radio|stereo|sound|bose|harman|revel|premium sound|infotainment|onstar|sync|uconnect|connect|link|bluetooth|usb|auxiliary|siriusxm|cd|mp3)/i.test(f)) {
    categories.infotainmentAudio.push(feature);
  }

  // 12. Navigation & Display
  if (/(navigation|head-up display)/i.test(f)) {
    categories.navigationDisplay.push(feature);
  }

  // 13. Security & Anti-Theft
  if (/(alarm|anti-theft|theft|security system)/i.test(f)) {
    categories.securityAntiTheft.push(feature);
  }

  // 14. Towing & Off-Road
  if (/(towing|tow|off-road|z71)/i.test(f)) {
    categories.towingOffRoad.push(feature);
  }

  // 15. Packages & Trims
  if (/(pkg|package|big horn|technology|premium)/i.test(f) && !/sound|audio/i.test(f)) {
    categories.packages.push(feature);
  }

  // 16. Wheels & Tires
  if (/wheels:|alloy|steel/i.test(f)) {
    categories.wheelsTires.push(feature);
  }
};

// Categorize all features
features.forEach(feature => {
  if (feature) categorizeFeature(feature);
});

// Build allowed photo categories based on equipment
const allowedPhotoCategories = [
  'hero_exterior',
  'engine_bay',
  'infotainment_other',
  'instrument_cluster',
  'steering_wheel',
  'front_seats',
  'second_row_seats',
  'cargo_area'
];

// Add conditional categories based on equipment
if (categories.infotainmentAudio.some(f => /carplay|android auto/i.test(f))) {
  allowedPhotoCategories.push('infotainment_carplay');
}

if (categories.driverAssist.some(f => /360|multi-view|surround view|around view/i.test(f))) {
  allowedPhotoCategories.push('infotainment_360_camera');
}

if (categories.roofsGlass.some(f => /moonroof|sunroof|panoramic/i.test(f))) {
  allowedPhotoCategories.push('moonroof');
}

if (categories.seatingConfig.some(f => /third row|3rd row|7 passenger|8 passenger/i.test(f))) {
  allowedPhotoCategories.push('third_row_seats');
}

// Always add special_feature category for unexpected finds
allowedPhotoCategories.push('special_feature');

// Build equipment context for each photo category
const equipmentContextByCategory = {
  hero_exterior: {},

  engine_bay: {
    engine_features: categories.enginePower,
    transmission: categories.transmission[0] || null,
    drivetrain: categories.drivetrain[0] || null
  },

  front_seats: {
    has_leather: categories.seatingConfig.some(f => /^leather$/i.test(f)),
    has_heated: categories.seatingConfig.some(f => /heated/i.test(f) && /seat/i.test(f)),
    has_cooled: categories.seatingConfig.some(f => /(cooled|ventilated)/i.test(f)),
    has_power: categories.seatingConfig.some(f => /(power|dual power)/i.test(f) && /seat/i.test(f)),
    has_memory: categories.seatingConfig.some(f => /memory/i.test(f)),
    material_claim: categories.seatingConfig.some(f => /^leather$/i.test(f)) ? 'leather' : 'premium upholstery',
    all_seat_features: categories.seatingConfig
  },

  second_row_seats: {
    seating_features: categories.seatingConfig.filter(f => /rear|second|passenger/i.test(f))
  },

  third_row_seats: {
    has_third_row: categories.seatingConfig.some(f => /third row|3rd row/i.test(f))
  },

  steering_wheel: {
    has_heated_wheel: categories.interiorComfort.some(f => /steering wheel.*heated/i.test(f)),
    has_controls: categories.interiorComfort.some(f => /steering wheel control/i.test(f)),
    tilt_telescoping: categories.interiorComfort.some(f => /tilt.*telescoping/i.test(f))
  },

  infotainment_carplay: {
    has_navigation: categories.navigationDisplay.length > 0,
    audio_system: categories.infotainmentAudio.find(f => /sound|audio|bose|harman|revel/i.test(f)),
    connectivity: categories.infotainmentAudio.filter(f => /bluetooth|usb|siriusxm/i.test(f))
  },

  infotainment_360_camera: {
    has_360: true,
    parking_features: categories.driverAssist.filter(f => /parking|park assist/i.test(f))
  },

  moonroof: {
    moonroof_type: categories.roofsGlass.find(f => /panoramic/i.test(f)) ? 'Panoramic' : 'Moonroof',
    all_roof_features: categories.roofsGlass
  },

  cargo_area: {
    power_liftgate: categories.interiorComfort.some(f => /power liftgate/i.test(f)),
    truck_bed_features: categories.exteriorLighting.filter(f => /bed liner|tailgate|tonneau/i.test(f))
  }
};

// Clean up baseConfig - remove equipment
const cleanBaseConfig = {
  ...config,
  equipment: undefined,  // Remove equipment string
  vehicles: config.vehicles?.map(vehicle => {
    const {equipment, ...cleanVehicle } = vehicle;
    return cleanVehicle;
  })
};

return [{
  json: {
    ...cleanBaseConfig,
    equipment_categories: categories,
    allowed_photo_categories: allowedPhotoCategories,
    equipment_context_by_category: equipmentContextByCategory,
  }
}];

const config = $json;
const vehicle = config.vehicles[0];
// Use thumbnails for faster processing
const allImageUrls = vehicle.all_images
  .filter(url => url && url.trim())
  .map(url => url + '/thumb/');

const allowedCategories = config.allowed_photo_categories;
const totalPhotos = allImageUrls.length;
const maxIndex = totalPhotos - 1;

const systemPrompt = `You are categorizing ${totalPhotos} photos of a ${vehicle.year} ${vehicle.make} ${vehicle.model}.

EQUIPMENT config CONFIRMS THIS VEHICLE HAS:
${config.equipment_categories.seatingConfig.some(f => /third row|3rd row/i.test(f)) ? 'âœ“ Third row seating\n' : ''}
${config.equipment_categories.roofsGlass.some(f => /moonroof|sunroof|panoramic/i.test(f)) ? 'âœ“ ' + config.equipment_context_by_category.moonroof.moonroof_type + '\n' : ''}
${config.equipment_categories.driverAssist.some(f => /360|multi-view|surround/i.test(f)) ? 'âœ“ 360-degree camera\n' : ''}
${config.equipment_categories.seatingConfig.some(f => /heated/i.test(f) && /seat/i.test(f)) ? 'âœ“ Heated seats\n' : ''}
${config.equipment_categories.seatingConfig.some(f => /cooled|ventilated/i.test(f)) ? 'âœ“ Cooled/ventilated seats\n' : ''}
${config.equipment_categories.seatingConfig.some(f => /^leather$/i.test(f)) ? 'âœ“ Leather seats\n' : ''}

Photo[0] is designated as hero_exterior (already selected).

CATEGORIES TO LOOK FOR:
${allowedCategories.filter(c => c !== 'hero_exterior').map(cat => `- ${cat}`).join('\n')}

DO NOT look for categories not listed above - they don't exist on this vehicle.

RULES:
1. Multiple photos CAN have the same category if showing DIFFERENT subcategories
   Example: One photo shows CarPlay, another shows 360 camera â†’ both are infotainment
2. If multiple photos show THE SAME THING, pick ONLY THE BEST (clearest, best lighting)
3. Only assign categories from the list above
4. Mark irrelevant photos as "exclude"

EXCLUDE these categories entirely (never ad-worthy):
- Center console storage compartments (close-ups)
- Control button close-ups (seat controls, climate buttons)
- Cup holder close-ups (showing just cupholders)
- Key fobs
- Window stickers / price labels
- Door panels (unless showing special feature)
- Random detail shots with no clear feature
- Photos that are blurry or too dark to use

QUALITY REQUIREMENTS:
- Well-lit photos only (skip dark or blurry)
- Clear view of features being highlighted
- Professional appearance (avoid awkward angles)
- Shows vehicle in good condition

CATEGORY DEFINITIONS:

ENGINE BAY:
- Clear shot of engine compartment
- Well-lit, shows engine condition

INFOTAINMENT (can have multiple):
- infotainment_carplay: Screen showing Apple CarPlay OR Android Auto interface clearly
- infotainment_360_camera: Screen showing 360-degree/bird's eye parking camera view
- infotainment_other: Touchscreen showing other features (navigation, audio, settings)

DASHBOARD:
- instrument_cluster: Gauge cluster or digital instrument display
- steering_wheel: Steering wheel (with or without controls visible)

SEATING:
- front_seats: Front seat view showing material, condition, and controls
- second_row_seats: Back seat view
- third_row_seats: Third row seating (only if equipment confirms it exists)

CARGO:
- cargo_area: Trunk or cargo space (or truck bed for pickups)

SPECIAL:
- moonroof: Moonroof open OR moonroof controls visible (only if equipment confirms)
- special_feature: Anything else impressive (heads-up display, wireless charging, etc.)

OUTPUT FORMAT (JSON only, no markdown):
{
  "categorized_photos": [
    {
      "photo_index": 1,
      "category": "engine_bay",
      "reasoning": "Clear engine shot, well-lit"
    },
    {
      "photo_index": 12,
      "category": "infotainment_carplay",
      "reasoning": "Shows Apple CarPlay interface clearly"
    },
    {
      "photo_index": 8,
      "category": "exclude",
      "reasoning": "Center console storage - not ad-worthy"
    }
  ],
  "summary": {
    "engine_bay": [1],
    "infotainment_carplay": [12],
    "front_seats": [20],
    "excluded": [8, 9, 22]
  }
}`;

const userPrompt = `Categorize all ${totalPhotos} photos of this ${vehicle.year} ${vehicle.make} ${vehicle.model}.

ğŸš¨ CRITICAL - PHOTO INDICES ğŸš¨
VALID INDICES: 0 through ${maxIndex}
TOTAL PHOTOS: ${totalPhotos}
DO NOT USE indices ${maxIndex + 1} or higher (they don't exist!)

Photo[0] is hero_exterior (skip this one).
Analyze photos 1 through ${maxIndex} and assign categories or mark as exclude.

Select the BEST photo for each category (quality over quantity).
If multiple photos show different features, keep both.
If multiple photos show the same feature, pick only the best one.`;

// Build vision_content array for Claude API
const visionContent = [
  {
    type: "text",
    text: `${systemPrompt}\n\n${userPrompt}\n\nPHOTOS (each labeled with its index):`
  }
];

// Add all images with explicit index labels
allImageUrls.forEach((url, index) => {
  visionContent.push({
    type: "text",
    text: `[PHOTO INDEX ${index}]`
  });
  visionContent.push({
    type: "image",
    source: {
      type: "url",
      url: url
    }
  });
});

console.log(`âœ… Built vision content with ${allImageUrls.length} photos`);

return [{
  json: {
    ...config,

    categorizer_vision_content: visionContent,
    categorizer_system_prompt: systemPrompt,
    categorizer_user_prompt: userPrompt,
    all_image_urls: allImageUrls,
    max_photo_index: maxIndex,
    total_photos: totalPhotos,

    // Remove used data
    equipment_categories: undefined,
    allowed_photo_categories: undefined
  }
}];

const config = $json;
let vehicles = config.vehicles;
const timing = config.timing;
const themeSection = config.theme_section;
const systemPrompt = $('Assemble System Prompt').first()?.json?.system_prompt || '';

if (!vehicles) {
  throw new Error('Missing vehicle data');
}

if (!systemPrompt) {
  throw new Error('No system prompt from Assemble System Prompt node');
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// VEHICLE FEATURE ENHANCEMENT (car_dealer vertical)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

vehicles = vehicles.map(vehicle => {
  let features = [...(vehicle.features || [])];

  const mileage = parseInt(vehicle.mileage) || 0;
  const year = parseInt(vehicle.year) || 2020;
  const age = 2025 - year;

  // Low mileage (relative to age)
  const expectedMileage = age * 12000;
  if (mileage > 0 && mileage < expectedMileage * 0.6) {
    features.unshift('Low Mileage');
  }

  // Hybrid/electric
  const engine = (vehicle.engine || '').toLowerCase();
  if (engine.includes('hybrid') || engine.includes('electric')) {
    features.unshift('Hybrid Power');
  } else if (engine.includes('4-cyl') || engine.includes('turbo')) {
    features.push('Great Gas Mileage');
  }

  // AWD/4WD
  const drivetrain = (vehicle.drivetrain || '').toLowerCase();
  if (drivetrain.includes('awd') || drivetrain.includes('4wd') || drivetrain.includes('4x4')) {
    features.push('All-Wheel Drive');
  }

  // Reliable brands
  const reliableBrands = ['toyota', 'honda', 'mazda', 'subaru', 'lexus'];
  if (reliableBrands.includes(vehicle.make.toLowerCase())) {
    features.push('Reliable Brand');
  }

  return {
    ...vehicle,
    features: features.length > 0 ? features : ['Great Condition']
  };
});

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// WORD LIMITS (dynamic based on vehicle count)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const totalWords = timing.hook + (timing.segment * vehicles.length) + timing.cta;

const wordLimitsSection = `

â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
âš ï¸ WORD LIMITS FOR THIS SCRIPT (CRITICAL)
â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

HOOK: ${timing.hook} words max
SEGMENT: ${timing.segment} words max PER CAR
CTA: ${timing.cta} words max

TOTAL: ${timing.hook} + (${timing.segment} Ã— ${vehicles.length} cars) + ${timing.cta} = ${totalWords} words

STYLE (${config.script_length}): ${timing.style}
`;

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// BUILD USER PROMPT
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const userPrompt = `Generate a ${config.script_length.toUpperCase()} multi-car ad script.

${themeSection}

VEHICLES (${vehicles.length}):
${vehicles.map((v, i) => `${i + 1}. ${v.year} ${v.make} ${v.model}
   Features: ${v.features.slice(0, 5).join(', ')}`).join('\n')}

WORD LIMITS:
- Hook: ${timing.hook} words max
- Each segment: ${timing.segment} words max  
- CTA: ${timing.cta} words max
- TOTAL: ${totalWords} words max

OUTPUT JSON:
{
  "hook": "Punchy opener (${timing.hook} words max)",
  "segments": [${vehicles.map(v => `"${v.year} ${v.make} â€” feature"`).join(', ')}],
  "cta": "Dealership + contact + financing (${timing.cta} words max)",
  "full_script": "Complete script",
  "notes": "Brief notes"
}`;

return [{
  json: {
    ...config,
    vehicles,
    system_prompt: systemPrompt + wordLimitsSection,
    user_prompt: userPrompt,
    word_limits: {
      total: totalWords,
      hook: timing.hook,
      segment: timing.segment,
      cta: timing.cta
    }
  }
}];

const config = $json;
const vehicle = config.vehicles[0];
const verifiedFeatures = config.verified_features || {};
const selectedPhotosObject = config.selected_photos || {};
const scriptLength = config.script_length || 'standard';
const timing = config.timing; // from Prepare Script Context

// Helper function to remove /thumb/ suffix
const getFullResUrl = (url) => {
  return url.replace('/thumb/', '/');
};

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// TRANSFORM SELECTED PHOTOS TO ARRAY FORMAT
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const selectedPhotosArray = [];

// Add hero first
if (selectedPhotosObject.hero_exterior) {
  selectedPhotosArray.push({
    photo_url: getFullResUrl(selectedPhotosObject.hero_exterior.photo_url),
    photo_index: selectedPhotosObject.hero_exterior.photo_index,
    category: "exterior_hero",
    feature_callout: "",
    display_order: 1
  });
}

// Priority order for features (most important first)
const priorityOrder = [
  'engine_bay',
  'moonroof',
  'infotainment_360_camera',
  'infotainment_carplay',
  'front_seats',
  'steering_wheel',
  'instrument_cluster',
  'cargo_area',
  'second_row_seats',
  'third_row_seats',
  'special_feature',
  'infotainment_other'
];

// Add features in priority order
let displayOrder = 2;
priorityOrder.forEach(category => {
  const featureData = verifiedFeatures[category];
  const photoData = selectedPhotosObject[category];

  if (featureData?.script_features && photoData) {
    selectedPhotosArray.push({
      photo_url: getFullResUrl(photoData.photo_url),
      photo_index: photoData.photo_index,
      category: category,
      feature_callout: featureData.script_features,
      display_order: displayOrder++
    });
  }
});

// Limit to top 5 features (plus hero = 6 total)
const finalPhotos = selectedPhotosArray.slice(0, 6);
const featureCount = finalPhotos.length - 1; // minus hero

