const config = $('Build Feature Identifier Prompts').first().json;
const apiResponse = $json.content[0].text;
const vehicle = config.vehicles[0];

// Parse JSON response (strip markdown if present)
let responseText = apiResponse.replace(/```json\n?/g, "").replace(/```\n?/g, "").trim();

// Try to extract JSON object
const jsonMatch = responseText.match(/\{[\s\S]*\}/);
if (jsonMatch) {
  responseText = jsonMatch[0];
}

const identifiedFeatures = JSON.parse(responseText);

// Combine equipment context + visual verification
const verifiedFeatures = {};

Object.entries(identifiedFeatures).forEach(([category, features]) => {
  const photoData = config.selected_photos[category];
  if (!photoData) {
    console.log(`âš ï¸ No photo data found for category: ${category}`);
    return;
  }

  verifiedFeatures[category] = {
    photo_index: photoData.photo_index,
    photo_url: photoData.photo_url,
    equipment_context: photoData.equipment_context,
    visual_verification: features,

    // Build final feature list for script generation
    script_features: buildScriptFeatures(category, photoData.equipment_context, features, vehicle)
  };
});

// Function to build script-ready feature descriptions
function buildScriptFeatures(category, equipment, visual, vehicle) {
  switch(category) {
    case 'front_seats':
      const seatFeatures = [];

      // Add condition if excellent
      if (visual.condition === 'excellent') {
        seatFeatures.push('pristine');
      }

      // Add material from equipment ONLY
      seatFeatures.push(equipment.material_claim || 'comfortable seating');

      // Add climate features
      if (equipment.has_heated && equipment.has_cooled) {
        seatFeatures.push('heated and ventilated');
      } else if (equipment.has_heated) {
        seatFeatures.push('heated');
      } else if (equipment.has_cooled) {
        seatFeatures.push('ventilated');
      }

      // Add power if present
      if (equipment.has_power) {
        seatFeatures.push('power-adjustable');
      }

      // Add memory if present
      if (equipment.has_memory) {
        seatFeatures.push('with memory');
      }

      return seatFeatures.join(' ');

    case 'engine_bay':
      const engineFeatures = [];

      // Add engine details from equipment
      if (equipment.engine_features && equipment.engine_features.length > 0) {
        const engineInfo = equipment.engine_features[0];
        // Extract key parts (e.g., "V6 i-VTEC 3.5 Liter")
        const match = engineInfo.match(/(V\d+|4-Cyl|3-Cyl|I\d+).*?(\d+\.\d+)\s*Liter/i);
        if (match) {
          const cylinders = match[1];
          const displacement = match[2];
          engineFeatures.push(`${displacement}L ${cylinders}`);
        } else {
          engineFeatures.push(engineInfo);
        }
      }

      // Add turbo/special tech
      if (equipment.engine_features && equipment.engine_features.some(e => /turbo/i.test(e))) {
        engineFeatures.push('turbocharged');
      }
      if (equipment.engine_features && equipment.engine_features.some(e => /hemi/i.test(e))) {
        engineFeatures.push('HEMI power');
      }

      // Add condition note
      if (visual.condition === 'clean') {
        engineFeatures.push('well-maintained');
      }

      return engineFeatures.join(', ');

    case 'infotainment_carplay':
      const infoFeatures = [];

      // Smartphone integration
      if (visual.has_apple_carplay && visual.has_android_auto) {
        infoFeatures.push('Apple CarPlay and Android Auto');
      } else if (visual.has_apple_carplay) {
        infoFeatures.push('Apple CarPlay');
      } else if (visual.has_android_auto) {
        infoFeatures.push('Android Auto');
      } else {
        infoFeatures.push('smartphone connectivity');
      }

      // Premium audio
      if (equipment.audio_system && /bose|harman|revel|premium/i.test(equipment.audio_system)) {
        infoFeatures.push(equipment.audio_system);
      }

      // Navigation
      if (equipment.has_navigation || visual.navigation_visible) {
        infoFeatures.push('navigation');
      }

      return infoFeatures.join(' with ');

    case 'infotainment_360_camera':
      const cameraFeatures = ['360-degree camera'];

      if (visual.parking_sensors_visible) {
        cameraFeatures.push('with parking sensors');
      }

      return cameraFeatures.join(' ');

    case 'moonroof':
      const roofFeatures = [];

      // Type from equipment
      if (equipment.moonroof_type === 'Panoramic' || visual.appears_panoramic) {
        roofFeatures.push('panoramic moonroof');
      } else {
        roofFeatures.push('power moonroof');
      }

      return roofFeatures.join(' ');

    case 'cargo_area':
      const cargoFeatures = [];

      // Space rating
      if (visual.space_rating === 'spacious' || visual.space_rating === 'huge') {
        cargoFeatures.push('spacious cargo area');
      } else {
        cargoFeatures.push('cargo space');
      }

      // Power liftgate
      if (equipment.power_liftgate) {
        cargoFeatures.push('with power liftgate');
      }

      // Truck bed features
      if (equipment.truck_bed_features && equipment.truck_bed_features.length > 0) {
        if (equipment.truck_bed_features.some(f => /bed liner/i.test(f))) {
          cargoFeatures.push('spray-in bed liner');
        }
        if (equipment.truck_bed_features.some(f => /multipro/i.test(f))) {
          cargoFeatures.push('MultiPro tailgate');
        }
      }

      return cargoFeatures.join(', ');

    case 'steering_wheel':
      const wheelFeatures = [];

      if (equipment.has_heated_wheel || visual.controls_visible?.heated) {
        wheelFeatures.push('heated steering wheel');
      }

      if (visual.controls_visible?.audio || visual.controls_visible?.cruise) {
        wheelFeatures.push('with steering wheel controls');
      }

      if (visual.paddle_shifters) {
        wheelFeatures.push('and paddle shifters');
      }

      return wheelFeatures.length > 0 ? wheelFeatures.join(' ') : 'multifunction steering wheel';

    case 'second_row_seats':
      const secondRowFeatures = [];

      if (visual.space_rating === 'spacious' || visual.space_rating === 'generous') {
        secondRowFeatures.push('spacious rear seating');
      } else {
        secondRowFeatures.push('rear seating');
      }

      if (visual.features_visible && visual.features_visible.length > 0) {
        if (visual.features_visible.some(f => /climate/i.test(f))) {
          secondRowFeatures.push('with rear climate control');
        }
      }

      return secondRowFeatures.join(' ');

    case 'third_row_seats':
      return 'third row seating for seven passengers';

    case 'instrument_cluster':
      if (visual.cluster_type === 'digital') {
        return 'digital instrument cluster';
      } else if (visual.cluster_type === 'hybrid') {
        return 'advanced gauge cluster with digital display';
      }
      return null; // Don't highlight basic analog clusters

    case 'special_feature':
      if (visual.feature_identified) {
        return visual.feature_identified;
      }
      return null;

    case 'infotainment_other':
      const otherFeatures = [];

      if (visual.showing === 'navigation' || equipment.has_navigation) {
        otherFeatures.push('navigation system');
      }

      if (equipment.audio_system && /bose|harman|revel|premium/i.test(equipment.audio_system)) {
        otherFeatures.push(equipment.audio_system);
      }

      if (otherFeatures.length === 0) {
        otherFeatures.push('touchscreen infotainment');
      }

      return otherFeatures.join(' with ');

    default:
      return null;
  }
}

