const input = $json.body;

// Validate required fields
if (!input.avatar_id) {
  throw new Error('avatar_id is required');
}
if (!input.preview_video_url) {
  throw new Error('preview_video_url is required');
}

return [{
  json: {
    avatar_id: input.avatar_id,
    motion_avatar_id: input.motion_avatar_id,
    person_id: input.person_id,
    style_name: input.style_name || 'avatar',
    preview_video_url: input.preview_video_url
  }
}];

// Prepare input for Replicate Alpha Video service
const input = $json;

return [{
  json: {
    video_url: input.preview_video_url,
    _context: {
      avatar_id: input.avatar_id,
      person_id: input.person_id,
      style_name: input.style_name
    }
  }
}];

const context = $('Prep: Replicate Input').item.json._context;
const replicateResult = $json;

if (!replicateResult.alpha_video_url) {
  throw new Error('Replicate did not return alpha_video_url');
}

return [{
  json: {
    file_url: replicateResult.alpha_video_url,
    upload_preset: 'avatar_preview_alpha_video',
    public_id: `avatar-alpha/${context.avatar_id}`,
    resource_type: 'video',
    _context: context
  }
}];

const context = $('Prep: Cloudinary Input').item.json._context;
const cloudinaryResult = $json;

if (!cloudinaryResult.secure_url) {
  throw new Error('Cloudinary did not return secure_url');
}

return [{
  json: {
    avatar_id: context.avatar_id,
    person_id: context.person_id,
    style_name: context.style_name,
    preview_video_alpha_url: cloudinaryResult.secure_url,
    cloudinary_public_id: cloudinaryResult.public_id
  }
}];

const input = $('Extract Cloudinary URL').item.json;

console.log('Alpha video generated and saved:', {
  avatar_id: input.avatar_id,
  preview_video_alpha_url: input.preview_video_alpha_url
});

return [{
  json: {
    success: true,
    avatar_id: input.avatar_id,
    preview_video_alpha_url: input.preview_video_alpha_url
  }
}];

const context = $('Parse Input').item.json;
let errorMsg = 'Unknown error';

// Try to get error from previous node
try {
  const prevJson = $json;
  errorMsg = prevJson.message || prevJson.error || JSON.stringify(prevJson);
} catch (e) {
  errorMsg = 'Error during alpha video processing';
}

return [{
  json: {
    workflow_name: '[06] Avatar Approved',
    error: errorMsg,
    details: {
      avatar_id: context.avatar_id,
      style_name: context.style_name,
      preview_video_url: context.preview_video_url
    },
    job_id: context.avatar_id
  }
}];

