const input = $json;

// Validate required fields
if (!input.file_url) {
  throw new Error('file_url is required');
}
if (!input.upload_preset) {
  throw new Error('upload_preset is required');
}

// Default resource_type to 'video' if not specified
const resourceType = input.resource_type || 'video';

return [{
  json: {
    file_url: input.file_url,
    upload_preset: input.upload_preset,
    resource_type: resourceType,
    public_id: input.public_id || null
  }
}];

const input = $json;
const cloudName = 'dtpqxuwby';
const url = `https://api.cloudinary.com/v1_1/${cloudName}/${input.resource_type}/upload`;

// Build form data
const formData = new FormData();
formData.append('file', input.file_url);
formData.append('upload_preset', input.upload_preset);

// Only add public_id if provided
if (input.public_id) {
  formData.append('public_id', input.public_id);
}

const response = await fetch(url, {
  method: 'POST',
  body: formData
});

const result = await response.json();

if (result.error) {
  throw new Error(`Cloudinary error: ${result.error.message}`);
}

return [{
  json: {
    secure_url: result.secure_url,
    public_id: result.public_id,
    resource_type: result.resource_type,
    format: result.format,
    bytes: result.bytes,
    width: result.width,
    height: result.height,
    duration: result.duration
  }
}];

