const config = $json;
const script = config.script;
const vehicles = config.vehicles;

if (!script.hook || !script.cars || !script.cta) {
  throw new Error('Script missing required sections');
}

// Build minimal sections array - ONLY what ElevenLabs needs
const sections = [
  { 
    name: 'hook',
    text: script.hook,
    index: 0
  },
  ...script.cars.map((carText, index) => ({
