const config = $json;
const audioSections = config._audio_sections;

if (!audioSections || audioSections.length === 0) {
  throw new Error('No audio sections to concatenate!');
}

console.log(`Concatenating ${audioSections.length} audio sections...`);

// Convert all base64 audio to buffers
const audioBuffers = audioSections.map(section => {
  if (!section.audio_base64) {
    throw new Error(`Section ${section.section_name} missing audio_base64!`);
  }
  return Buffer.from(section.audio_base64, 'base64');
});

console.log('Audio buffers created:', audioBuffers.map((buf, i) => 
  `${audioSections[i].section_name}: ${(buf.length / 1024).toFixed(2)}KB`
).join(', '));

// Simple concatenation: MP3 files can be concatenated by joining the data
// This works because MP3 frames are self-contained
const concatenatedBuffer = Buffer.concat(audioBuffers);

// Convert back to base64
const concatenatedBase64 = concatenatedBuffer.toString('base64');

const totalSize = concatenatedBuffer.length;
console.log(`✅ Concatenated audio size: ${(totalSize / 1024).toFixed(2)} KB`);

return [{
  json: {
    ...config,
    outputs: {
      ...(config.outputs || {}),
      audio_base64: concatenatedBase64
    }
  }
}];

const config = $json;
const audioSections = $input.first().json._audio_sections;

if (!audioSections || audioSections.length === 0) {
  throw new Error('No audio sections found!');
}

console.log('Calculating timings for', audioSections.length, 'sections');

// Calculate cumulative timings
let currentTime = 0;
const timings = [];

for (const section of audioSections) {
  const startTime = currentTime;
  const endTime = currentTime + section.duration_seconds;

  timings.push({
    section_name: section.section_name,
    start_time: Math.round(startTime),
    end_time: Math.round(endTime),
    duration: Math.round(section.duration_seconds)
  });

  currentTime = endTime;

  console.log(`${section.section_name}: ${Math.round(startTime)}s - ${Math.round(endTime)}s`);
}

// Extract car timings specifically (sections with VINs)
const carTimings = timings.filter(t => 
  t.section_name !== 'hook' && t.section_name !== 'cta'
);

console.log('Car sections found:', carTimings.length);

// Update vehicles with accurate timings
const updatedVehicles = config.vehicles.map((vehicle, index) => {
  const carTiming = carTimings[index];

  if (!carTiming) {
    console.warn(`No timing found for vehicle ${index + 1} (${vehicle.vin})`);
    return vehicle;
  }

  console.log(`Vehicle ${vehicle.vin}: ${carTiming.start_time}s - ${carTiming.end_time}s`);

  return {
    ...vehicle,
    start_time: carTiming.start_time,
    end_time: carTiming.end_time
  };
});

const totalDuration = Math.round(currentTime);

console.log(`✅ Total audio duration: ${totalDuration}s`);
console.log(`✅ Updated ${updatedVehicles.length} vehicles with timings`);

return [{
  json: {
    ...config,
    vehicles: updatedVehicles,
    _audio_sections: audioSections,
    _section_timings: timings,
    outputs: {
      ...(config.outputs || {}),
      audio_duration: totalDuration
    }
  }
}];

