const fileSizeBytes = binaryData.length;
const BITRATE_KBPS = 128;
const durationSeconds = (fileSizeBytes * 8) / (BITRATE_KBPS * 1000);
const roundedDuration = Math.round(durationSeconds * 100) / 100;

console.log(`File size: ${fileSizeBytes} bytes`);
console.log(`Calculated duration: ${durationSeconds}s`);
console.log(`Rounded duration: ${roundedDuration}s`);

if (roundedDuration === 0 || !roundedDuration) {
  console.warn('⚠️ Duration is 0 or null! File might be too small.');
}

const inputData = $json;
const binaryItems = inputData.items || [];

console.log('Processing', binaryItems.length, 'binary items');

const audioSections = [];

for (let i = 0; i < binaryItems.length; i++) {
  const item = binaryItems[i];

  const name = item.name;
  const text = item.text;
  const index = item.index;
  const audioBase64 = item.audio_base64; // Already provided by Combine!

  if (!audioBase64) {
    console.warn(`No audio_base64 for ${name}`);
    continue;
  }

  // Parse file size from the file_size field
  const fileSizeStr = item.file_size;
  let fileSizeBytes;

  if (fileSizeStr && fileSizeStr.includes('kB')) {
    const kb = parseFloat(fileSizeStr.replace(' kB', ''));
    fileSizeBytes = Math.round(kb * 1024);
  } else if (fileSizeStr && fileSizeStr.includes('MB')) {
    const mb = parseFloat(fileSizeStr.replace(' MB', ''));
    fileSizeBytes = Math.round(mb * 1024 * 1024);
  } else {
    // Calculate from base64 length as fallback
    fileSizeBytes = Math.round((audioBase64.length * 3) / 4);
  }

  const BITRATE_KBPS = 128;
  const durationSeconds = (fileSizeBytes * 8) / (BITRATE_KBPS * 1000);
  const roundedDuration = Math.round(durationSeconds * 100) / 100;

  console.log(`Section "${name}": ${fileSizeBytes} bytes = ${roundedDuration}s`);

  audioSections.push({
    section_name: name,
    section_index: index,
    section_text: text,
    audio_base64: audioBase64,
    duration_seconds: roundedDuration,
    file_size_bytes: fileSizeBytes
  });
}

audioSections.sort((a, b) => a.section_index - b.section_index);

console.log('Extracted durations for', audioSections.length, 'sections');

return [{
  json: {
    _audio_sections: audioSections
  }
}];

const allItems = $input.all();

console.log('Combining', allItems.length, 'binary items');

const binaryItems = [];

for (let i = 0; i < allItems.length; i++) {
  const item = allItems[i];

  // Get the actual binary buffer while we still have access
  let binaryBuffer = null;

  try {
    binaryBuffer = await this.helpers.getBinaryDataBuffer(i, 'data');
    console.log(`✅ Got buffer ${i} (${item.json.name}): ${binaryBuffer.length} bytes`);
  } catch (error) {
    console.warn(`❌ Failed buffer ${i}:`, error.message);
  }

  // Convert to base64 NOW while we have the buffer
  const audioBase64 = binaryBuffer ? binaryBuffer.toString('base64') : null;

  binaryItems.push({
    name: item.json.name,
    text: item.json.text,
    index: item.json.index,
    audio_base64: audioBase64, // Store base64 here!
    file_size: item.binary?.data?.fileSize
  });
}

return [{
  json: {
    items: binaryItems
  }
}];
