// Single error - format it for the email
const error = $input.first().json;
const nodeName = error.node?.name || 'Unknown node';

// Handle different error formats from different APIs
let errorMsg = 'Unknown error';

// Claude error format: { "error": { "type": "...", "message": "..." }, "request_id": "..." }
if (error.error?.type && error.error?.message) {
  errorMsg = `[${error.error.type}] ${error.error.message}`;
  if (error.request_id) {
    errorMsg += ` (Request ID: ${error.request_id})`;
  }
}
// Cloudinary error format: { "error": { "message": "..." } }
else if (error.error?.message && !error.error?.type) {
  errorMsg = error.error.message;
}
// Remove.bg error format: { "errors": [{ "code": "...", "title": "..." }] }
else if (error.errors && Array.isArray(error.errors) && error.errors.length > 0) {
  const firstError = error.errors[0];
  // Include code if present, otherwise just title
  errorMsg = firstError.code 
    ? `${firstError.code}: ${firstError.title}` 
    : firstError.title;
}
// Generic message fallback
else if (error.message) {
  errorMsg = error.message;
}

return [{
  json: {
    nodeName: nodeName,
    errorMessage: errorMsg,
    timestamp: new Date().toISOString(),
    rawError: JSON.stringify(error, null, 2) // Include full error for debugging
  }
}];

