const body = $input.first().json.body;
const theme = body.theme || 'abstract gradient';
const clientId = body.client_id;
const style = body.style || 'hybrid';
const clarity = body.clarity || 'balanced';
const headline = body.headline || '';
const subtext = body.subtext || '';

// Clarity instructions
const clarityMap = {
  subtle: 'Include 1 faint symbolic motif related to the theme, subtly integrated into the design.',
  balanced: 'Include 1-2 clear theme objects that are recognizable but not overwhelming.',
  bold: 'Include 2-4 prominent, instantly recognizable theme objects.'
};
const clarityInstruction = clarityMap[clarity] || clarityMap.balanced;

// Universal safe zone - visible in all aspect ratios (9:16, 4:5, 16:9)
const safeZoneRules = `CRITICAL PLACEMENT - UNIVERSAL SAFE ZONE:
All text, focal elements, and key theme objects MUST be placed in the CENTER-RIGHT area:
- Horizontally: Right 50-60% of the image
- Vertically: Middle 35% of the image (not too high, not too low)
This ensures visibility when cropped to any aspect ratio (9:16, 4:5, or 16:9).
Do NOT place important elements near the top or bottom edges.`;

// Base composition rules (shared across styles)
const compositionRules = `COMPOSITION:
- Create a SEAMLESS, CONTINUOUS background that flows naturally across the entire image
- NO panels, sections, dividers, frames, or distinct left/right areas
- Use a GRADIENT OF COMPLEXITY: subtle/minimal on the left, more detailed toward the right
- The left side should have the SAME style, texture, and colors as the rest, just softer/simpler
- A person will be composited on the left later, so avoid placing focal elements there
- Think of it like a single cohesive scene, not a split layout`;

const doNotInclude = `DO NOT INCLUDE:
- Any text, words, letters, symbols or numbers (unless Typography style)
- Recognizable faces or people
- Logos, brand marks, or trademarked imagery
- Busy patterns that would compete with a speaker
- Any frames, boxes, panels, UI elements, or placeholder shapes
- Gray or colored rectangles, dividers, or solid color sections
- Distinct left/right split layouts or panels`;

let prompt = '';

switch (style) {
  case 'abstract':
    prompt = `Create an abstract, artistic vertical background image for a video advertisement.

THEME: "${theme}"

STYLE: Abstract gradients, bokeh effects, flowing shapes, painterly. NOT photorealistic.
${clarityInstruction}

${safeZoneRules}

${compositionRules}

COLORS: Professional palette matching the theme.

${doNotInclude}`;
    break;

  case 'hybrid':
    prompt = `Create a vertical background for a video advertisement.

THEME: "${theme}"

STYLE: Abstract gradients + bokeh + flowing shapes, combined with recognizable theme elements.
${clarityInstruction}
Make theme objects large enough to recognize at a glance, with soft depth-of-field and glow accents.

${safeZoneRules}

${compositionRules}

COLORS: Professional palette matching theme.

${doNotInclude}`;
    break;

  case 'illustration':
    prompt = `Create a bold, clean, illustrated vertical background.

THEME: "${theme}"

STYLE: Modern vector/flat illustration, crisp shapes, limited color palette, soft grain.
${clarityInstruction}

${safeZoneRules}
Cluster iconic objects in the center-right safe zone - big, readable, not busy.

${compositionRules}

${doNotInclude}`;
    break;

  case 'photorealistic':
    prompt = `Create a photorealistic vertical background image for a video ad.

THEME: "${theme}"

STYLE: Cinematic photography look, shallow depth-of-field, professional lighting.
${clarityInstruction}
Include theme-relevant objects (large, recognizable).
Use generic/non-identifiable environment (no real landmarks, no readable signage).

${safeZoneRules}

${compositionRules}

${doNotInclude}
- No identifiable locations, license plates, or readable signage`;
    break;

  case 'texture':
    prompt = `Create a minimal vertical background with premium texture and subtle theme cues.

THEME: "${theme}"

STYLE: Material/texture focus (paper, brushed metal, linen, concrete, wood grain), soft lighting, gentle bokeh.
${clarityInstruction}

${safeZoneRules}
Place subtle theme object in the center-right safe zone.

${compositionRules}

${doNotInclude}`;
    break;

  case 'typography':
    prompt = `Create a bold typographic vertical background poster for a video ad.

CRITICAL - TEXT POSITIONING:
A person will be standing on the LEFT side of this image later.
Place ALL text on the OPPOSITE side from where the person will stand.
The person takes up the left 40-50%, so put text in the remaining space.

TEXT LAYOUT (IMPORTANT):
- Keep headline COMPACT - use 2-3 lines maximum, NOT one word per line
- Group multiple words on each line (e.g., "IT'S TAX" on line 1, "RETURN TIME!!" on line 2)
- Subtext must appear DIRECTLY BELOW the headline, not separated
- Keep headline and subtext together as ONE text block, vertically centered
- Do NOT spread text across the full height of the image

HEADLINE TEXT (render exactly): "${headline}"
${subtext ? `SUBTEXT directly below headline (render exactly): "${subtext}"` : ''}
THEME: "${theme}"

TEXT STYLE:
- Bold, condensed sans-serif font
- High contrast for readability
- Headline and subtext grouped tightly together

BACKGROUND:
- Seamless, continuous background across entire image
- Beautiful bokeh, gradients, or theme-appropriate imagery
- Professional color palette matching the theme
- NO panels, frames, dividers, or split layouts

DO NOT:
- Put one word per line (too tall)
- Separate subtext from headline
- Place text where a person would stand (left side)
- Place text near top or bottom edges`;
    break;


  default:
    prompt = `Create an abstract vertical background for: "${theme}"`;
}

// Build the complete request body for Gemini
const requestBody = {
  generationConfig: {
    responseModalities: ["IMAGE"],
    imageConfig: {
      aspectRatio: "9:16"
    }
  },
  contents: [{
    parts: [{
      text: prompt
    }]
  }]
};

return [{ json: { requestBody, theme, clientId, style, clarity } }];

const response = $input.first().json;
const theme = $('Build Prompt').first().json.theme;
const clientId = $('Build Prompt').first().json.clientId;

let imageData = null;
let mimeType = 'image/png';

if (response.candidates && response.candidates[0]) {
  const parts = response.candidates[0].content?.parts || [];
  for (const part of parts) {
    if (part.inlineData) {
      imageData = part.inlineData.data;
      mimeType = part.inlineData.mimeType || 'image/png';
      break;
    }
  }
}

if (!imageData) {
  throw new Error('No image in response: ' + JSON.stringify(response).substring(0, 500));
}

return [{ json: { imageData, mimeType, theme, clientId } }];

const cloudinaryResponse = $input.first().json;
const theme = $('Extract Image').first().json.theme;
const clientId = $('Extract Image').first().json.clientId;

if (cloudinaryResponse.error) {
  throw new Error('Cloudinary failed: ' + cloudinaryResponse.error.message);
}

return [{ json: {
  success: true,
  theme: theme,
  client_id: clientId,
  cloudinary_url: cloudinaryResponse.secure_url,
  cloudinary_public_id: cloudinaryResponse.public_id,
  width: cloudinaryResponse.width,
  height: cloudinaryResponse.height
}}];

