// Define all persons to sync
const persons = [
  { person_id: '11111111-1111-1111-1111-111111111111', name: 'Gary', heygen_group_id: '7c9b17d0db574a6aa4e7fc8a2cf71ce8' },
  { person_id: '22222222-2222-2222-2222-222222222222', name: 'Shad', heygen_group_id: '98c258ddd69741f6a2fa1964e22383a3' },
  { person_id: '33333333-3333-3333-3333-333333333333', name: 'Kelly', heygen_group_id: '585545c8034c4c43b3969797841efd6c' },
  { person_id: '00000000-0000-0000-0000-000000000000', name: 'Brooklyn', heygen_group_id: 'a59d7191cd0d454b840a63695fc73abf' }
];

return persons.map(p => ({ json: p }));

// Get persons list and HeyGen responses
const personsList = $('List Persons').all();
const heygenResponses = $input.all();

const MAX_URL_LEN = 490;
const allResults = [];

// Process each HeyGen response with its corresponding person
for (let i = 0; i < heygenResponses.length; i++) {
  const person = personsList[i]?.json;
  const heygenData = heygenResponses[i]?.json;

  if (!person || !heygenData) continue;

  const personId = person.person_id;
  const personName = person.name;
  const avatarList = heygenData.data?.avatar_list || [];

  for (const avatar of avatarList) {
    // Only include motion avatars
    if (!avatar.is_motion) continue;

    // Use avatar name as style name (no motion type suffix)
    const styleName = (avatar.name || 'Default').replace(/'/g, "''");

    // Set URLs to null if too long
    let previewUrl = avatar.motion_preview_url || null;
    if (previewUrl && previewUrl.length > MAX_URL_LEN) previewUrl = null;
    if (previewUrl) previewUrl = previewUrl.replace(/'/g, "''");

    let imageUrl = avatar.image_url || null;
    if (imageUrl && imageUrl.length > MAX_URL_LEN) imageUrl = null;
    if (imageUrl) imageUrl = imageUrl.replace(/'/g, "''");

    allResults.push({
      json: {
        person_id: personId,
        person_name: personName,
        style_name: styleName,
        motion_avatar_id: avatar.id,
        preview_video_url: previewUrl,
        preview_image_url: imageUrl
      }
    });
  }
}

return allResults.length > 0 ? allResults : [{ json: { skip: true } }];

// Count total avatars processed
const allData = $input.all();
let syncedCount = 0;

for (const item of allData) {
  const data = item.json.data || [];
  syncedCount += data.filter(d => d && d.id).length;
}

return [{
  json: {
    success: true,
    message: `Synced ${syncedCount} avatars from HeyGen to database`,
    synced_count: syncedCount
  }
}];

