// Extract the Gemini response
const geminiResponse = $input.item.json.candidates?.[0]?.content?.parts?.[0]?.text || '';
const finishReason = $input.item.json.candidates?.[0]?.finishReason || 'unknown';

let result;
let parseError = null;

try {
  let cleaned = geminiResponse.trim();

  // Remove markdown code blocks if present
  if (cleaned.startsWith('```json')) {
    cleaned = cleaned.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  } else if (cleaned.startsWith('```')) {
    cleaned = cleaned.replace(/```\n?/g, '').trim();
  }

  result = JSON.parse(cleaned);
} catch (error) {
  parseError = error.message;

  // Try to extract partial data
  const seedMatch = geminiResponse.match(/"theme_seed"\s*:\s*"([^"]+)"/);
  const emojiMatch = geminiResponse.match(/"emoji"\s*:\s*"([^"]+)"/);
  const dirMatch = geminiResponse.match(/"direction"\s*:\s*"([^"]+)"/);

  result = {
    theme_seed: seedMatch ? seedMatch[1] : 'Weekly Theme',
    emoji: emojiMatch ? emojiMatch[1] : 'ğŸš—',
    direction: dirMatch ? dirMatch[1] : 'Connect with customers this week',
    _warning: 'Partial parse - response may have been truncated',
    _parse_error: parseError
  };
}

// Get local_override_text from Parse Request
const localOverrideText = $('Parse Request').item.json.local_override_text;

return {
  success: !!result.theme_seed,
  mode: 'seed',
  theme_seed: result.theme_seed,
  emoji: result.emoji,
  direction: result.direction,
  local_override_text: localOverrideText || null
};

